import "../css/common.css"
import "../css/Join.css"
import { Link } from "react-router-dom";
import React, { useState } from "react";
import { useDispatch } from "react-redux";


function Join(){
    //초기값 세팅 - 아이디, 비밀번호, 비밀번호 확인, 이메일
    const [id, setId] = useState("");
    const [pw, setPw] = useState("");
    const [pwConfirm, setPwConfirm] = useState("");
    const [email, setEmail] = useState("");

    //오류메세지 상태 저장
    const [idMessage, setIdMessage] = useState("");
    const [pwMessage, setPwMessage] = useState("");
    const [pwConfirmMessage, setPwConfirmMessage] = useState("");
    const [emailMessage, setEmailMessage] = useState("");

    //유효성 검사
    const [isId, setIsId] = useState(false);
    const [isname, setIsName] = useState(false);
    const [isPw, setIsPw] = useState(false);
    const [isPwConfirm ,setIsPwConfirm] = useState(false);
    const [isEmail, setIsEmail] = useState(false);

    //에러메세지 띄우기
    const onChangeId = (e) => {
        const currentId = e.target.value;
        setId(currentId);
        const idRegExp = /^[a-zA-Z0-9]{4,10}$/;

        if(!idRegExp.test(currentId)){
            setIdMessage("4~10글자 사이 대소문자 또는 숫자를 입력해주세요!");
            setIsId(false);
        } else{
            setIdMessage("사용가능한 아이디 입니다.");
            setIsId(true);
        }
    }

    return(
        <div className="join_main">
            <div className="joinBox">
                <div>
                    <p className="join_text">회원가입하기</p>
                    <div className="join_login">
                        <p>계정이 있으신가요?</p>
                        <Link to={"/login"}><p className="join_login-button">로그인</p></Link>
                    </div>
                </div>

                <input placeholder="아이디"></input>
                <div className="join_name">
                    <input placeholder="성" onChange={(e)=> {setId(id)}}></input>
                    <input placeholder="이름"></input>
                </div>
                <input placeholder="이메일"></input>
                <input placeholder="비밀번호"></input>
                <input placeholder="비밀번호 확인"></input>
                <button className="joinConfirm">안전과 함께 가입하기</button>
            </div>
        </div>
    )
}

export default Join;
